  Col,
  Row,
  Form,
  InputGroup,
  FormControl,
  Table,
} from "react-bootstrap";
import {
  addTransaction,
  searchTokenByNameOrAddress,
  startLoading,
  stopLoading,
} from "../../redux/actions";
import { ReferralsServices } from "../../services/ReferralsServices";
import { FarmService } from "../../services/FarmService";
